mapper.select:
		SELECT NUM, INSTITUTIONID, INSTITUTIONNAME, INSTITUTIONADDRESS, INSTITUTIONCALL
		FROM ( 
			 SELECT a.*, rownum num
           	 FROM (
				  SELECT * 
				  FROM TTR_INSTITUTION
           	      ) a
       		 ORDER BY #{sortname} ${sortorder}
       		 )
		WHERE rownum BETWEEN #{start} AND #{end}
